    function getBytesSlot(bytes storage store) internal pure returns (BytesSlot storage r) {
        assembly ("memory-safe") {
            r.slot := store.slot
        }
    }
}

contracts/AuditTrail.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

/**
 * @title AuditTrail
 * @dev Immutable audit logging contract for healthcare data access tracking
 * @notice Implements UUPS upgradeable proxy with gas-optimized event-based logging
 * 
 * Data Structures:
 * - AuditEntry: Packed struct for efficient storage
 * - AuditType: Enum for different audit event types
 * 
 * Security Features:
 * - ReentrancyGuard: Prevents reentrancy attacks
 * - AccessControl: Role-based access
 * - Immutable logs via events (gas optimization)
 * - Batch operations for efficiency
 */
contract AuditTrail is 
    UUPSUpgradeable, 
    AccessControlUpgradeable, 
    ReentrancyGuardUpgradeable 
{
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant AUDITOR_ROLE = keccak256("AUDITOR_ROLE");

    enum AuditType {
        DataAccess,     // 0
        ConsentChange,  // 1
        AIDiagnostic,   // 2
        SystemEvent     // 3
    }

    // Packed struct for gas optimization
    struct AuditEntry {
        bytes32 entryId;        // 32 bytes
        AuditType auditType;    // 1 byte
        address actorId;         // 20 bytes
        bytes32 resourceId;      // 32 bytes
        uint64 timestamp;        // 8 bytes
        bool granted;            // 1 byte
        // Total: ~94 bytes
    }

    // State variables
    mapping(bytes32 => AuditEntry) private _auditEntries;
    mapping(bytes32 => bytes32[]) private _resourceAuditTrails; // resourceId => entryId[]
    mapping(address => bytes32[]) private _actorAuditTrails;    // actorId => entryId[]

    // Events for efficient off-chain querying
    event AuditLogged(
        bytes32 indexed entryId,
        AuditType indexed auditType,
        address indexed actorId,
        bytes32 resourceId,
        uint64 timestamp,
        bool granted,
        string reason
    );

    event BatchAuditLogged(
        bytes32[] entryIds,
        AuditType auditType,
        uint64 timestamp
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address admin) public initializer {
        __UUPSUpgradeable_init();
        __AccessControl_init();
        __ReentrancyGuard_init();

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);
    }

    /**
     * @dev Log a data access event
     * @param actorId Address of the entity accessing data
     * @param resourceId ID of the resource being accessed
     * @param granted Whether access was granted
     * @param reason Reason for grant/denial
     * @return entryId The ID of the audit entry
     */
    function logDataAccess(
        address actorId,
        bytes32 resourceId,
        bool granted,
        string calldata reason
    ) 
        external 
        nonReentrant 
        returns (bytes32 entryId) 
    {
        require(actorId != address(0), "Invalid actor address");
        require(resourceId != bytes32(0), "Invalid resource ID");

        entryId = _createAuditEntry(
            AuditType.DataAccess,
            actorId,
            resourceId,
            granted
        );

        emit AuditLogged(
            entryId,
            AuditType.DataAccess,
            actorId,
            resourceId,
            uint64(block.timestamp),
            granted,
            reason
        );

        return entryId;
    }

    /**
     * @dev Log a consent change event
     * @param actorId Address of the entity making the change
     * @param consentId ID of the consent record
     * @param action Action taken (granted/revoked)
     * @return entryId The ID of the audit entry
     */
    function logConsentChange(
        address actorId,
        bytes32 consentId,
        string calldata action
    ) 
        external 
        nonReentrant 
        returns (bytes32 entryId) 
    {
        require(actorId != address(0), "Invalid actor address");
        require(consentId != bytes32(0), "Invalid consent ID");

        entryId = _createAuditEntry(
            AuditType.ConsentChange,
            actorId,
            consentId,
            true // Consent changes are always "granted" (action happened)
        );

        emit AuditLogged(
            entryId,
            AuditType.ConsentChange,
            actorId,
            consentId,
            uint64(block.timestamp),
            true,
            action
        );

        return entryId;
    }

    /**
     * @dev Log an AI diagnostic event
     * @param modelId ID of the AI model
     * @param recordId ID of the medical record
     * @param confidence Confidence score (0-10000, representing 0.00-100.00%)
     * @return entryId The ID of the audit entry
     */
    function logAIDiagnostic(
        bytes32 modelId,
        bytes32 recordId,
        uint16 confidence
    ) 
        external 
        nonReentrant 
        returns (bytes32 entryId) 
    {
        require(modelId != bytes32(0), "Invalid model ID");
        require(recordId != bytes32(0), "Invalid record ID");
        require(confidence <= 10000, "Invalid confidence score");

        // Use modelId as actorId (address(0) for system actors)
        entryId = _createAuditEntry(
            AuditType.AIDiagnostic,
            address(0), // System actor
            recordId,
            true
        );

        emit AuditLogged(
            entryId,
            AuditType.AIDiagnostic,
            address(0),
            recordId,
            uint64(block.timestamp),
            true,
            string(abi.encodePacked("Model: ", _bytes32ToString(modelId), " Confidence: ", _uint16ToString(confidence)))
        );

        return entryId;
    }

    /**
     * @dev Get audit entry by ID
     * @param entryId ID of the audit entry
     * @return entry The audit entry
     */
    function getAuditEntry(bytes32 entryId)
        external
        view
        returns (AuditEntry memory entry)
    {
        entry = _auditEntries[entryId];
        require(entry.entryId != bytes32(0), "Audit entry not found");
    }

    /**
     * @dev Get audit trail for a resource
     * @param resourceId ID of the resource
     * @return entryIds Array of audit entry IDs
     */
    function getResourceAuditTrail(bytes32 resourceId)
        external
        view
        returns (bytes32[] memory entryIds)
    {
        return _resourceAuditTrails[resourceId];
    }

    /**
     * @dev Get audit trail for an actor
     * @param actorId Address of the actor
     * @return entryIds Array of audit entry IDs
     */
    function getActorAuditTrail(address actorId)
        external
        view
        returns (bytes32[] memory entryIds)
    {
        return _actorAuditTrails[actorId];
    }

    /**
     * @dev Internal function to create audit entry
     */
    function _createAuditEntry(
        AuditType auditType,
        address actorId,
        bytes32 resourceId,
        bool granted
    ) 
        internal 
        returns (bytes32 entryId) 
    {
        entryId = keccak256(
            abi.encodePacked(
                actorId,
                resourceId,
                block.timestamp,
                block.number,
                gasleft()
            )
        );

        AuditEntry memory entry = AuditEntry({
            entryId: entryId,
            auditType: auditType,
            actorId: actorId,
            resourceId: resourceId,
            timestamp: uint64(block.timestamp),
            granted: granted
        });

        _auditEntries[entryId] = entry;
        _resourceAuditTrails[resourceId].push(entryId);
        _actorAuditTrails[actorId].push(entryId);

        return entryId;
    }

    /**
     * @dev Helper to convert bytes32 to string (for events)
     */
    function _bytes32ToString(bytes32 _bytes32) internal pure returns (string memory) {
        uint8 i = 0;
        while(i < 32 && _bytes32[i] != 0) {
            i++;
        }
        bytes memory bytesArray = new bytes(i);
        for (i = 0; i < 32 && _bytes32[i] != 0; i++) {
            bytesArray[i] = _bytes32[i];
        }
        return string(bytesArray);
    }

    /**
     * @dev Helper to convert uint16 to string
     */
    function _uint16ToString(uint16 _i) internal pure returns (string memory) {
        if (_i == 0) {
            return "0";
        }
        uint16 j = _i;
        uint16 len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint16 k = len;
        while (_i != 0) {
            k = k-1;
            uint8 temp = (48 + uint8(_i - _i / 10 * 10));
            bytes1 b1 = bytes1(temp);
            bstr[k] = b1;
            _i /= 10;
        }
        return string(bstr);
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyRole(ADMIN_ROLE)
    {}

    function version() external pure returns (string memory) {
        return "1.0.0";
    }
}

contracts/ConsentManagement.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

/**
 * @title ConsentManagement
 * @dev Smart contract for managing patient consent on the blockchain
 * @notice Implements UUPS upgradeable proxy pattern with reentrancy protection
 * 
 * Data Structures:
 * - ConsentRecord: Stores consent information with expiration tracking
 * - ConsentStatus: Enum for consent states (None, Granted, Revoked, Expired)
 * 
 * Security Features:
 * - ReentrancyGuard: Prevents reentrancy attacks
 * - AccessControl: Role-based access control
 * - UUPS Proxy: Upgradeable contract pattern
 * - Gas Optimization: Packed structs, events for off-chain queries
 */
contract ConsentManagement is 
    UUPSUpgradeable, 
    AccessControlUpgradeable, 
    ReentrancyGuardUpgradeable 
{
    // Role definitions
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant CLINICIAN_ROLE = keccak256("CLINICIAN_ROLE");
    bytes32 public constant PATIENT_ROLE = keccak256("PATIENT_ROLE");

    // Consent status enum
    enum ConsentStatus {
        None,      // 0
        Granted,   // 1
        Revoked,   // 2
        Expired    // 3
    }

    // Consent types enum
    enum ConsentType {
        DataAccess,    // 0
        AIAnalysis,    // 1
        Research,     // 2
        Sharing        // 3
    }

    // Consent record structure - packed for gas optimization
    struct ConsentRecord {
        bytes32 consentId;        // 32 bytes
        address patientId;        // 20 bytes
        address clinicianId;      // 20 bytes
        ConsentType consentType;  // 1 byte
        ConsentStatus status;     // 1 byte
        uint64 grantedAt;         // 8 bytes
        uint64 expiresAt;         // 8 bytes (0 = no expiration)
        uint32 purpose;           // 4 bytes (encoded purpose)
        // Total: ~94 bytes (fits in 2 storage slots)
    }

    // State variables
    uint256 private _consentIdCounter;

    // Mapping: patientId => clinicianId => consentType => consentId
    mapping(address => mapping(address => mapping(ConsentType => bytes32))) 
        private _activeConsents;

    // Mapping: consentId => ConsentRecord
    mapping(bytes32 => ConsentRecord) private _consents;

    // Mapping: patientId => consentId[]
    mapping(address => bytes32[]) private _patientConsents;

    // Events for off-chain indexing (gas optimization)
    event ConsentGranted(
        bytes32 indexed consentId,
        address indexed patientId,
        address indexed clinicianId,
        ConsentType consentType,
        uint64 grantedAt,
        uint64 expiresAt
    );

    event ConsentRevoked(
        bytes32 indexed consentId,
        address indexed revokedBy,
        uint64 revokedAt
    );

    event ConsentExpired(
        bytes32 indexed consentId,
        address indexed patientId
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the contract (replaces constructor for upgradeable contracts)
     */
    function initialize(address admin) public initializer {
        __UUPSUpgradeable_init();
        __AccessControl_init();
        __ReentrancyGuard_init();

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);
    }

    /**
     * @dev Grant consent for a patient to a clinician
     * @param patientId Address of the patient
     * @param clinicianId Address of the clinician
     * @param consentType Type of consent being granted
     * @param expiresAt Expiration timestamp (0 = no expiration)
     * @param purpose Encoded purpose identifier
     * @return consentId The ID of the created consent record
     */
    function grantConsent(
        address patientId,
        address clinicianId,
        ConsentType consentType,
        uint64 expiresAt,
        uint32 purpose
    ) 
        external 
        nonReentrant 
        returns (bytes32 consentId) 
    {
        // Validate inputs
        require(patientId != address(0), "Invalid patient address");
        require(clinicianId != address(0), "Invalid clinician address");
        require(
            hasRole(CLINICIAN_ROLE, clinicianId) || 
            hasRole(PATIENT_ROLE, patientId),
            "Unauthorized"
        );

        // Check for existing active consent
        bytes32 existingConsentId = _activeConsents[patientId][clinicianId][consentType];
        if (existingConsentId != bytes32(0)) {
            ConsentRecord storage existing = _consents[existingConsentId];
            require(
                existing.status != ConsentStatus.Granted || 
                (existing.expiresAt > 0 && existing.expiresAt < block.timestamp),
                "Active consent already exists"
            );
        }

        // Generate new consent ID
        _consentIdCounter++;
        consentId = keccak256(
            abi.encodePacked(
                patientId,
                clinicianId,
                consentType,
                block.timestamp,
                _consentIdCounter
            )
        );

        // Create consent record
        ConsentRecord memory newConsent = ConsentRecord({
            consentId: consentId,
            patientId: patientId,
            clinicianId: clinicianId,
            consentType: consentType,
            status: ConsentStatus.Granted,
            grantedAt: uint64(block.timestamp),
            expiresAt: expiresAt,
            purpose: purpose
        });

        // Store consent
        _consents[consentId] = newConsent;
        _activeConsents[patientId][clinicianId][consentType] = consentId;
        _patientConsents[patientId].push(consentId);

        // Emit event
        emit ConsentGranted(
            consentId,
            patientId,
            clinicianId,
            consentType,
            newConsent.grantedAt,
            expiresAt
        );

        return consentId;
    }

    /**
     * @dev Revoke a consent
     * @param consentId ID of the consent to revoke
     */
    function revokeConsent(bytes32 consentId) 
        external 
        nonReentrant 
    {
        ConsentRecord storage consent = _consents[consentId];

        require(consent.consentId != bytes32(0), "Consent not found");
        require(
            consent.status == ConsentStatus.Granted,
            "Consent not active"
        );
        require(
            msg.sender == consent.patientId || 
            hasRole(ADMIN_ROLE, msg.sender),
            "Unauthorized to revoke"
        );

        // Update status
        consent.status = ConsentStatus.Revoked;

        // Clear active consent mapping
        delete _activeConsents[
            consent.patientId
        ][
            consent.clinicianId
        ][
            consent.consentType
        ];

        emit ConsentRevoked(consentId, msg.sender, uint64(block.timestamp));
    }

    /**
     * @dev Check if valid consent exists
     * @param patientId Address of the patient
     * @param clinicianId Address of the clinician
     * @param consentType Type of consent to check
     * @return exists True if valid consent exists
     */
    function hasValidConsent(
        address patientId,
        address clinicianId,
        ConsentType consentType
    ) 
        external 
        view 
        returns (bool exists) 
    {
        bytes32 consentId = _activeConsents[patientId][clinicianId][consentType];

        if (consentId == bytes32(0)) {
            return false;
        }

        ConsentRecord storage consent = _consents[consentId];

        // Check status
        if (consent.status != ConsentStatus.Granted) {
            return false;
        }

        // Check expiration
        if (consent.expiresAt > 0 && consent.expiresAt < block.timestamp) {
            return false;
        }

        return true;
    }

    /**
     * @dev Get consent record by ID
     * @param consentId ID of the consent
     * @return record The consent record
     */
    function getConsent(bytes32 consentId)
        external
        view
        returns (ConsentRecord memory record)
    {
        record = _consents[consentId];
        require(record.consentId != bytes32(0), "Consent not found");
    }

    /**
     * @dev Get all consent IDs for a patient
     * @param patientId Address of the patient
     * @return consentIds Array of consent IDs
     */
    function getPatientConsents(address patientId)
        external
        view
        returns (bytes32[] memory consentIds)
    {
        return _patientConsents[patientId];
    }

    /**
     * @dev Authorize upgrade (UUPS pattern)
     */
    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyRole(ADMIN_ROLE)
    {}

    /**
     * @dev Get contract version
     */
    function version() external pure returns (string memory) {
        return "1.0.0";
    }
}

contracts/DataIntegrity.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/ReentrancyGuardUpgradeable.sol";

/**
 * @title DataIntegrity
 * @dev Smart contract for storing Merkle roots and verifying data integrity
 * @notice Implements UUPS upgradeable proxy with gas-optimized Merkle root storage
 * 
 * Data Structures:
 * - MerkleRootRecord: Stores Merkle root with metadata
 * 
 * Security Features:
 * - ReentrancyGuard: Prevents reentrancy attacks
 * - AccessControl: Role-based access
 * - Immutable root storage
 */
contract DataIntegrity is 
    UUPSUpgradeable, 
    AccessControlUpgradeable, 
    ReentrancyGuardUpgradeable 
{
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");

    struct MerkleRootRecord {
        bytes32 root;           // 32 bytes
        bytes32 recordSetId;    // 32 bytes (identifier for the set of records)
        uint64 timestamp;       // 8 bytes
        uint32 recordCount;     // 4 bytes
        address storedBy;       // 20 bytes
        // Total: ~96 bytes
    }

    // State variables
    mapping(bytes32 => MerkleRootRecord) private _merkleRoots; // root => record
    mapping(bytes32 => bytes32[]) private _recordSetRoots;    // recordSetId => root[]
    bytes32[] private _allRoots; // For enumeration

    event MerkleRootStored(
        bytes32 indexed root,
        bytes32 indexed recordSetId,
        address indexed storedBy,
        uint32 recordCount,
        uint64 timestamp
    );

    event MerkleRootVerified(
        bytes32 indexed root,
        bool verified,
        uint64 timestamp
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(address admin) public initializer {
        __UUPSUpgradeable_init();
        __AccessControl_init();
        __ReentrancyGuard_init();

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);
    }

    /**
     * @dev Store a Merkle root on-chain
     * @param root The Merkle root hash
     * @param recordSetId Identifier for the set of records
     * @param recordCount Number of records in the set
     * @return success True if stored successfully
     */
    function storeMerkleRoot(
        bytes32 root,
        bytes32 recordSetId,
        uint32 recordCount
    ) 
        external 
        nonReentrant 
        returns (bool success) 
    {
        require(root != bytes32(0), "Invalid root");
        require(recordSetId != bytes32(0), "Invalid record set ID");
        require(recordCount > 0, "Invalid record count");
        require(
            hasRole(ADMIN_ROLE, msg.sender) || 
            hasRole(VERIFIER_ROLE, msg.sender),
            "Unauthorized"
        );

        // Check if root already exists
        require(_merkleRoots[root].root == bytes32(0), "Root already stored");

        MerkleRootRecord memory record = MerkleRootRecord({
            root: root,
            recordSetId: recordSetId,
            timestamp: uint64(block.timestamp),
            recordCount: recordCount,
            storedBy: msg.sender
        });

        _merkleRoots[root] = record;
        _recordSetRoots[recordSetId].push(root);
        _allRoots.push(root);

        emit MerkleRootStored(
            root,
            recordSetId,
            msg.sender,
            recordCount,
            record.timestamp
        );

        return true;
    }

    /**
     * @dev Verify if a Merkle root exists
     * @param root The Merkle root to verify
     * @return exists True if root exists
     * @return record The Merkle root record if exists
     */
    function verifyMerkleRoot(bytes32 root)
        external
        view
        returns (bool exists, MerkleRootRecord memory record)
    {
        record = _merkleRoots[root];
        exists = record.root != bytes32(0);
        // Note: Events cannot be emitted in view functions
    }

    /**
     * @dev Get Merkle root record
     * @param root The Merkle root
     * @return record The Merkle root record
     */
    function getMerkleRootRecord(bytes32 root)
        external
        view
        returns (MerkleRootRecord memory record)
    {
        record = _merkleRoots[root];
        require(record.root != bytes32(0), "Root not found");
    }

    /**
     * @dev Get all roots for a record set
     * @param recordSetId The record set ID
     * @return roots Array of Merkle roots
     */
    function getRecordSetRoots(bytes32 recordSetId)
        external
        view
        returns (bytes32[] memory roots)
    {
        return _recordSetRoots[recordSetId];
    }

    /**
     * @dev Get total number of stored roots
     * @return count Total number of roots
     */
    function getTotalRoots() external view returns (uint256 count) {
        return _allRoots.length;
    }

    /**
     * @dev Get root at index (for enumeration)
     * @param index The index
     * @return root The Merkle root
     */
    function getRootAtIndex(uint256 index)
        external
        view
        returns (bytes32 root)
    {
        require(index < _allRoots.length, "Index out of bounds");
        return _allRoots[index];
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyRole(ADMIN_ROLE)
    {}

    function version() external pure returns (string memory) {
        return "1.0.0";
    }
}

